//
// Pokemon typing logic
//
class PokeType {
    constructor(author, color, supereffective, noteffective, resistant, weak) {
        this.emb = new Discord.RichEmbed()
            .setAuthor(author)
            .setColor(color)
            .addField("Super Effective Against:", supereffective)
            .addField("Not Very Effective Against:", noteffective)
            .addField("Resistant to:", resistant)
            .addField("Weak to:", weak)
    }
}

//
// Weather typing logic
//

class WeatherType {
    constructor(author, boostedType) {
        this.emb = new Discord.RichEmbed()
            .setAuthor(author)
            .addField("Boosted Types:", boostedType)
    }
}

//
// Pokemon logic
//
class Pokemon {
    constructor(pokeID, color, types, weatherBoost, shiny, activeBoss, raidLevel, raidCP, soloInfo, baseATK, baseDEF, baseSTA, topCounters, weakTo, resistantTo, moveSetGrades, lvl15CP, lvl20CP, lvl25CP, lvl30CP, lvl35CP, lvl40CP) {
        this.emb = new Discord.RichEmbed()
            .setAuthor(`#${pokeID} ${names[pokeID-1]}`, `https:\/\/raw.githubusercontent.com/gladiator10864/PokeAlarm/master/icons/${pokeID}.png`)
            .setColor(color)
            .setThumbnail(`https:\/\/raw.githubusercontent.com/kvangent/gladiator10864/master/icons/${pokeID}.png`)
        if (raidLevel > 0 || raidLevel === "EX Invite Only") {
            if (raidLevel === 3) {
                this.emb.setDescription("**Type: **" + types + "\nBoosted In: " + weatherBoost + "\nShiny: " + shiny + "\nRaid Level: " + raidLevel + " (" + activeBoss + ")"  + "\nRaid Boss CP: " + raidCP + "\nSolo: " + soloInfo)
            } else {
                this.emb.setDescription("**Type: **" + types + "\nBoosted In: " + weatherBoost + "\nShiny: " + shiny + "\nRaid Level: " + raidLevel + " (" + activeBoss + ")"  + "\nRaid Boss CP: " + raidCP)
            }
        } else {
            this.emb.setDescription("**Type: **" + types + "\nBoosted In: " + weatherBoost + "\nShiny: " + shiny)
        }
        this.emb.addField("Base Stats:", "ATK: " + baseATK + "\nDEF: " + baseDEF + "\nSTA: " + baseSTA)
            .addField("Best Counters:", topCounters)
            .addField("Weak to:", weakTo)
            .addField("Resistant to:", resistantTo)
            .addField("Best Movesets (%, DMG, Moveset): ", moveSetGrades + '\n* = Legacy Move\n*Please see "More Info" for additional moveset ratings.*')
            .addField("Perfect CP's: ", "Lvl 15 = " + lvl15CP + "\nLvl 20 =  " + lvl20CP + "\nLvl 25 =  " + lvl25CP + "\nLvl 30 = " + lvl30CP + "\nLvl 35 = " + lvl35CP + "\nLvl 40 = " + lvl40CP + "\n\n*Last updated: " + updateDate + "\n[More Info](https:\/\/db.pokemongohub.net/pokemon/" + pokeID + ")*");
    }
}

